/* general styling */
html, body {
  margin: 0px;
  width: 100%;
  height: 100%;
  display: flex;
}

.flex {
  display: flex;
}

.col-1 {
  flex: 75;
}

.col-2 {
  flex: 25;
}

.panel {
  display: flex;
  flex: 1;
  justify-content: center;
  align-items: center;
  height: 100%;
}

.row {
  display: block;
}

form {
  border: 1px solid #ddd;
}

input,
input:focus,
input:visited,
input:active {
  border: none;
  background-color: #fff;
  outline: none;
  cursor: pointer;
}

input:hover {
  background-color: #f7e228;
  transition: all 1.2s;
}

/* calculator styling */
.area > input[type=text] {
  color: #000;
  background-color: #ddd;
  text-align: right;
  height: 48pt;
  font-size: 24pt;
}

.buttons {
  display: flex;
}

.button {
  flex: 24;
  /* border: 1px solid #ddd; */
  font-size: 24pt;
  padding: 12pt;
}

#showValue {
  padding-right: 8pt;
}

.button.reset {
  background-color: #f2575b;
}

.buttons.equal {
  display: flex;
  height: 40%;
}

.buttons.equal > .button {
  background-color: #2782bf;
}

/* media query for landscape display (fully responsive) */
@media only screen and (max-width: 768px) and (orientation: landscape) {
  .panel {
    display: block !important;
  }

  #showValue {
    width: 100%;
    padding-right: 0px;
  }

  .button {
    font-size: 18pt;
    padding: 8pt;
  }
}
